package com.singh.vinay.hadlercalleverysecond.server;import com.singh.vinay.hadlercalleverysecond.response.LoginResponse;import retrofit2.Call;import retrofit2.http.Field;import retrofit2.http.FormUrlEncoded;import retrofit2.http.POST;/** * All the APIs (Server calls through the App) are written here. */public interface ApiService {    /* Login */  @FormUrlEncoded  @POST("api")    Call<LoginResponse> login(@Field("mobile") String username, @Field("password") String password,@Field("action") String login);}